import Header from "./components/Header";
import Middle from "./components/Middle";
import Footer from "./components/Footer";
import axios from "axios";
export default {
  name: 'App',
  components: {
    Footer,
    Middle,
    Header
  },
  data: function () {
    return {
      user: null,
      postId: null,
      posts: [],
      users: []
    };
  },
  beforeMount() {
    if (localStorage.getItem("jwt") && !this.user) {
      this.$root.$emit("onJwt", localStorage.getItem("jwt"));
    }
    axios.get("/api/1/posts").then(response => {
      this.posts = response.data;
    });
    axios.get("/api/1/users").then(response => {
      this.users = response.data;
    });
  },
  beforeCreate() {
    this.$root.$on("onEnter", (login, password) => {
      if (password === "") {
        this.$root.$emit("onEnterValidationError", "Password is required");
        return;
      }
      axios.post("/api/1/jwt", {
        login,
        password
      }).then(response => {
        localStorage.setItem("jwt", response.data);
        this.$root.$emit("onJwt", response.data);
      }).catch(error => {
        this.$root.$emit("onEnterValidationError", error.response.data);
      });
    });
    this.$root.$on("onRegister", (login, name, password) => {
      if (password === "") {
        this.$root.$emit("onRegisterValidationError", "Password is required");
        return;
      }
      axios.post("/api/1/users", {
        login,
        name,
        password
      }).then(response => {
        //this.$root.$emit("onChangePage", "Index");
        this.$root.$emit("onEnter", response.data.login, response.data.password);
      }).catch(error => {
        this.$root.$emit("onRegisterValidationError", error.response.data);
      });
    });
    this.$root.$on("onWriteComment", text => {
      if (this.userId) {
        if (!this.postId) {
          this.$root.$emit("onWriteCommentValidationError", "ID is invalid");
        } else if (!text || text.trim().length < 10) {
          this.$root.$emit("onWriteCommentValidationError", "Text is too short");
        } else {
          axios.post("/api/1/post", {
            text,
            user: this.user,
            post: this.posts.filter(post => post.id === this.postId)
          }).then(() => {
            axios.get("/api/1/posts").then(response => {
              this.posts = response.data;
            });
            this.$root.$emit("onChangePage", "Index");
          }).catch(error => {
            this.$root.$emit("onWritePostValidationError", error.response.data);
          });
          const id = Math.max(...Object.keys(this.comments)) + 1;
          this.$root.$set(this.comments, id, {
            id,
            text,
            userId: this.userId,
            postId: this.postId
          });
          this.$root.$emit("onWriteCommentValidationError", "");
        }
      } else {
        this.$root.$emit("onWriteCommentValidationError", "No access");
      }
    });
    this.$root.$on("onWritePost", (title, text) => {
      if (this.user) {
        if (!title || title.trim().length < 5) {
          this.$root.$emit("onWritePostValidationError", "Title is too short");
        } else if (!text || text.trim().length < 10) {
          this.$root.$emit("onWritePostValidationError", "Text is too short");
        } else {
          axios.post("/api/1/posts", {
            title,
            text,
            user: this.user
          }).then(() => {
            axios.get("/api/1/posts").then(response => {
              this.posts = response.data;
            });
            this.$root.$emit("onChangePage", "Index");
          }).catch(error => {
            this.$root.$emit("onWritePostValidationError", error.response.data);
          });
        }
      } else {
        this.$root.$emit("onWritePostValidationError", "No access");
      }
    });
    this.$root.$on("onJwt", jwt => {
      localStorage.setItem("jwt", jwt);
      axios.get("/api/1/users/auth", {
        params: {
          jwt
        }
      }).then(response => {
        this.user = response.data;
        this.$root.$emit("onChangePage", "Index");
      }).catch(() => this.$root.$emit("onLogout"));
    });
    this.$root.$on("onLogout", () => {
      localStorage.removeItem("jwt");
      this.user = null;
    });
  }
};
