import Header from "./components/Header";
import Middle from "./components/Middle";
import Footer from "./components/Footer";
import axios from "axios";
export default {
  name: 'App',
  components: {
    Footer,
    Middle,
    Header
  },
  data: function () {
    return {
      user: null,
      posts: [],
      users: []
    };
  },
  beforeMount() {
    if (localStorage.getItem("jwt") && !this.user) {
      this.$root.$emit("onJwt", localStorage.getItem("jwt"));
    }
    axios.get("/api/1/posts").then(response => {
      this.posts = response.data;
    });
    axios.get("/api/1/users").then(response => {
      this.users = response.data;
    });
  },
  beforeCreate() {
    this.$root.$on("onEnter", (login, password) => {
      if (password === "") {
        this.$root.$emit("onEnterValidationError", "Password is required");
        return;
      }
      axios.post("/api/1/jwt", {
        login,
        password
      }).then(response => {
        localStorage.setItem("jwt", response.data);
        this.$root.$emit("onJwt", response.data);
      }).catch(error => {
        this.$root.$emit("onEnterValidationError", error.response.data);
      });
    });
    this.$root.$on("onRegister", (login, name) => {
      if (!login || login.length < 3 || login.length > 16) {
        this.$root.$emit("onRegisterValidationError", "Login length should be from 3 to 16 letters");
      } else if (!/^[a-z]+$/.test(login)) {
        this.$root.$emit("onRegisterValidationError", "Login should contain only lowercase letters");
      } else if (!name || name.length < 1 || name.length > 32) {
        this.$root.$emit("onRegisterValidationError", "Name or surname length should be from 1 to 32 letters");
      } else if (!/^[a-zA-Z]+[ ]+[a-zA-Z]+$/.test(name)) {
        this.$root.$emit("onRegisterValidationError", "Name should contain two words of only lowercase or uppercase letters");
      } else {
        const findUsers = Object.values(this.users).filter(u => u.login === login);
        if (findUsers.length != 0) {
          this.$root.$emit("onRegisterValidationError", "A user with this login already exists");
        } else {
          const id = Math.max(...Object.keys(this.users)) + 1;
          this.$root.$set(this.users, id, {
            id,
            login,
            name,
            admin: false
          });
          this.$root.$emit("onChangePage", "Enter");
        }
      }
    });
    this.$root.$on("onJwt", jwt => {
      localStorage.setItem("jwt", jwt);
      axios.get("/api/1/users/auth", {
        params: {
          jwt
        }
      }).then(response => {
        this.user = response.data;
        this.$root.$emit("onChangePage", "Index");
      }).catch(() => this.$root.$emit("onLogout"));
    });
    this.$root.$on("onLogout", () => {
      localStorage.removeItem("jwt");
      this.user = null;
    });
  }
};
